import { useState } from "react";
import "./work.scss";

export default function Works() {
  const [currentSlide, setCurrentSlide] = useState(0);
  const data = [
    {
      id: "1",
      icon: "./assets/mobile.png",
      title: "Coding",
      desc:
        "I love to participate in various coding contests and challenges and also contribute for open source. ",
      img:
        "data:image/png;base64,",
    },
    {
      id: "2",
      icon: "./assets/globe.png",
      title: "Table Tennis",
      desc:
        "I like to play table tennis and I was also selected for state level tournaments.",
      img:
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRw05xV_gtpOV7FgmwK2r6JCJmB6MQtjlpk6A&usqp=CAU",
    },
    {
      id: "3",
      icon: "./assets/writing.png",
      title: "Gaming",
      desc:
        "I play computer games in my free time. Usually the games are Apex Legends and Valorant.",
      img:
        "data:image/jpeg;base64,",
    },
  ];

  const handleClick = (way) => {
    way === "left"
      ? setCurrentSlide(currentSlide > 0 ? currentSlide - 1 : 2)
      : setCurrentSlide(currentSlide < data.length - 1 ? currentSlide + 1 : 0);
  };
  
  return (
    <div className="works" id="works">
      <h3>Hobbies</h3>
      <div
        className="slider"
        style={{ transform: `translateX(-${currentSlide * 100}vw)` }}
      >
        {data.map((d) => (
          <div className="container">
            <div className="item">
              <div className="left">
                <div className="leftContainer">
                  <div className="imgContainer">
                  <img src alt="" />
                  </div>
                  <h2>{d.title}</h2>
                  <p>{d.desc}</p>
                 
                </div>
              </div>
              <div className="right">
                <img
                  src={d.img}
                  alt=""
                />
              </div>
            </div>
          </div>
        ))}
      </div>
      <img
        src="assets/arrow.png"
        className="arrow left"
        alt=""
        onClick={() => handleClick("left")}
      />
      <img
        src="assets/arrow.png"
        className="arrow right"
        alt=""
        onClick={() => handleClick()}
      />
    </div>
  );
}